/**
 * Convex auth configuration.
 *
 * In development, uses a custom JWT provider with a static dev keypair
 * (generated by scripts/generate-dev-keys.ts). This lets ctx.auth.getUserIdentity()
 * work identically to production, enabling multi-user dev sessions.
 *
 * DO NOT manually edit the jwks value — it is auto-updated by:
 *   npm run generate-dev-keys
 *
 * In production, this will be replaced/extended with Better Auth OIDC config.
 */
export default {
  providers: [
    {
      type: 'customJwt',
      applicationID: 'floorplan-dev',
      issuer: 'https://floorplan-dev-auth.local',
      // AUTO-GENERATED — do not edit manually (run: npm run generate-dev-keys)
      // @generated-jwks-start
      jwks: 'data:text/plain;charset=utf-8;base64,',
      // @generated-jwks-end
      algorithm: 'RS256',
    },
  ],
};
